  if (expenses) {
    var numCards = expenses.length;
    for (var i = 0; i < numCards; i++) {
      var arr = expenses[i].date.split('-')
      var date = arr[2] + '-' + arr[1] + '-' + arr[0]
      expenseCardList.push(<ExpenseCard data = {[date, expenses[i].merchant, expenses[i].amount,
