import React, { Component } from 'react';
import Sidebar from './components/sidebar';
import ExpensesBody from './components/expenses_body';
import Landing from './landing';
import { BrowserRouter as Router, Route} from  "react-router-dom";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

    callAPI() {
      fetch("http://localhost:9000/users")
          .then(res => res.json())
          .then(res => this.setState({ apiResponse: res }));
  }

  componentWillMount() {
      this.callAPI();
  }

  loggedIn() {
    //Create a t/f state to check if user is logged in or not
    return true;
  }

  toArr() {
    const arr = this.state.apiResponse
    ? this.state.apiResponse.split(',')
    : [];
    return arr;
  }

  render () {
    if(this.loggedIn()){return(
      // <Router>
        <div class="App">
          <Sidebar/>
          {/* <Route path="/" exact component={ExpensesBody}/> */}
          {/* will add function to switch between reports and this */}
          <ExpensesBody /> 
        </div>
      // </Router>
    );}
    else{
      return(
        <Landing/>
      );
    }
  }
}

export default App;
