import React, { Component } from 'react';
import Sidebar from './components/sidebar';
import ExpensesBody from './components/expenses_body';
import Login from './login';
import NewExpense from './components/new_expense';
import { BrowserRouter as Router, Route} from  "react-router-dom";
import './App.css';
import Construction from './components/construction';
import { CommandCursor } from 'mongodb';

class App extends Component {

  state = {
    seen: false,
    cards: false
  };

toggleWindow = () => {
    this.setState({
      seen: !this.state.seen
    });
    console.log("Window Toggled");
  }

setCards = () => {
  this.setState({
    cards: true
  });
  console.log("Card List loaded");
}

  render () {
    if(sessionStorage.getItem('status')){return(
        <div class="App">
          {/* Construction is temp for production*/}
          {/* <Construction/> */}
          <Sidebar/>
          <ExpensesBody toggleWindow = {this.toggleWindow} currSeenState = {this.state.seen} setCardState = {this.setCards()}/> 
          {this.state.seen ? <NewExpense toggleWindow = {this.toggleWindow}/> : null}
        </div>
      // </Router>
    );}
    else{
      return(
        <Login/>
      );
    }
  }
}

export default App;
