import React, { Component } from 'react';
import Sidebar from './components/sidebar';
import ExpensesBody from './components/expenses_body';
import Login from './login';
import NewExpense from './components/new_expense';
import { BrowserRouter as Router, Route} from  "react-router-dom";
import './App.css';
import Construction from './components/construction';
import { CommandCursor } from 'mongodb';

class App extends Component {

  state = {
    seen: false
  };

toggleWindow = () => {
    this.setState({
      seen: !this.state.seen
    });
    console.log("Window Toggled");
  }

getCookie = cname => {
  var name = cname + "=";
  var decodedCookie = decodeURIComponent(document.cookie);
  var ca = decodedCookie.split(';');
  for(var i = 0; i <ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) == 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}

  render () {
    console.log(this.getCookie("status"))
    if(this.getCookie("status") == "true"){return(
        <div class="App">
          {/* Construction is temp for production*/}
          {/* <Construction/> */}
          <Sidebar/>
          <ExpensesBody toggleWindow = {this.toggleWindow} currSeenState = {this.state.seen}/> 
          {this.state.seen ? <NewExpense toggleWindow = {this.toggleWindow}/> : null}
        </div>
      // </Router>
    );}
    else{
      return(
        <Login/>
      );
    }
  }
}

export default App;
