import React, { Component } from 'react';

var account_body = () => {
    var changeEmail = () => {
        const requestOptions =
        {
          method: 'POST',
          headers: {
          },
          body: JSON.stringify({
            email: email,
            newEmail: newEmail
          })
        };
          fetch('https://api.expensey.app/users/expenses/del_expense',requestOptions) 
              .then(res => res.json())
              .then (data => 
                {
                    console.log(data);
                    if (data == 'Expense deleted successfully.'){
                        window.location.reload();
                    }
                })
              .catch(error => console.log(error));
    }

    var changePw = () => {
        const requestOptions =
        {
          method: 'POST',
          headers: {
          },
          body: JSON.stringify({
            email: email,
            newEmail: newEmail
          })
        };
          fetch('https://api.expensey.app/users/expenses/del_expense',requestOptions) 
              .then(res => res.json())
              .then (data => 
                {
                    console.log(data);
                    if (data == 'Expense deleted successfully.'){
                        window.location.reload();
                    }
                })
              .catch(error => console.log(error));
    }

    var delAccount = () => {
        const requestOptions =
        {
          method: 'POST',
          headers: {
          },
          body: JSON.stringify({
            email: email,
            newEmail: newEmail
          })
        };
          fetch('https://api.expensey.app/users/expenses/del_expense',requestOptions) 
              .then(res => res.json())
              .then (data => 
                {
                    console.log(data);
                    if (data == 'Expense deleted successfully.'){
                        window.location.reload();
                    }
                })
              .catch(error => console.log(error));
    }



    return (
        <div id="accountBody">
        <div id="changeEmail">
            <h2>Change Email</h2>
            <input type="text" placeholder="Enter new email address."></input>
            <button>Update Email</button>
        </div>
        <div id="resetPw">
            <h2>Change Password</h2>
            <input type="text" placeholder="Enter current password."></input>
            <input type="text" placeholder="Enter new password."></input>
            <input type="text" placeholder="Confirm password."></input>
            <button>Update password</button>
        </div>
        <h2>Delete Account</h2>
        <button id="delAccount">Delete Account</button>
        </div>
    )
}

export default account_body;
