import React from 'react';
import './settings_body.css';

const settings_body = props => {
    if (props.dkMode() == true) {
        var bgColor = "#121212";
        var txtColor = "white";
        var settings_body =  document.getElementsByClassName('settings_body')[0];
        var expenses_body = document.getElementsByClassName('ExpensesBody')[0];
        settings_body.style.backgroundColor = bgColor;
        expenses_body.style.backgroundColor = bgColor;
        settings_body.style.color = txtColor;
    }
    if (props.dkMode() == false) {
        var elem =  document.getElementsByClassName('settings_body')[0];
        if (elem) {
            elem.style.backgroundColor = "rgb(233, 233, 233)";
            elem.style.color = "black";
        }
    }
  return (
      <div class="settings_body">
      <div class="settings_header">
      <h2>Settings</h2>
      </div>
      <div class="interface_settings">
      <h3>Interface</h3>
        <div class="dkmode">
            <p>Dark Mode</p>
                <label class="switch">
                <input type="checkbox" onClick={props.toggleDarkMode}/>
                <span class="slider round"></span>
                </label>
        </div>
        </div>
      </div>
  )
}

export default settings_body;
