import React from 'react';
import './header.css';
import App from '../App';

const header = props => {

  function toggleWindow() {
   App.setState({
      seen: false 
    });
  };
  //TODO fix formatting in css and link button to new expense function
  return (
      <div className="Header">
        <h2>Expenses</h2>
        <button onClick={toggleWindow}>New expense</button>
      </div>
  )
}

export default header;
