import React from 'react';
import './settings_body.css';

const settings_body = props => {
    var bgColor;
    var txtColor;
    if (props.dkMode() == true) {
        bgColor = '#121212';
        txtColor = "white";
    }
    if (props.dkMode() == false) {
        var elem =  document.getElementsByClassName('settings_body')[0];
        if (elem) {
            bgColor = 'rgb(233, 233, 233)';
            txtColor = "black";
        }
    }
  return (
      <div class="settings_body" style={{backgroundColor : bgColor}}>
      <div class="settings_header">
      <h2>Settings</h2>
      </div>
      <div class="interface_settings">
      <h3>Interface</h3>
        <div class="dkmode">
            <p>Dark Mode</p>
                <label class="switch">
                <input type="checkbox" onClick={props.toggleDarkMode}/>
                <span class="slider round"></span>
                </label>
        </div>
        </div>
      </div>
  )
}

export default settings_body;
