import React from 'react';
import { useTable } from 'react-table'
import './table.css';

const Table = props => {
  //TODO connect database and table and finish writing appropriate functions
  function fetchListData(databaseVar /* temp var name for whatever I end up passing here */) {
    function fetchCatList(databaseListItems) {
      for (var i = 0; i <= databaseListItems.length(); i++) {
        listData.setCatList(databaseListItems[i]);
      }
    }
    function fetchCcList(databaseListItems) {
      for (var i = 0; i <= databaseListItems.length(); i++) {
        listData.setCctList(databaseListItems[i]);
      }
    }
    function fetchTagList(databaseListItems) {
      for (var i = 0; i <= databaseListItems.length(); i++) {
        listData.setTagList(databaseListItems[i]);
      }
    }
    function fetchAllList() {
      fetchCatList(); 
      fetchCcList(); 
      fetchTagList();
    }
    fetchAllList();
  }

  function listData (){
    var tagList = [];
    var categoriesList = [];
    var ccList = [];

    return {
      setTagList : function (newTagList) {
        tagList = newTagList;
      },
      setCatList : function (newCatList) {
        categoriesList = newCatList;
      },
      setCcList : function (newCcList) {
        ccList = newCcList;
      },
      getCategoriesList : function () {
        return categoriesList;
      },
      getCcList : function () {
        return  ccList;
      },
      getTagList : function () {
        return tagList;
      }
    };
  }

  const data = React.useMemo(
    () => [
      {
        col1: 'Hello',
        col2: 'World',
        col3: 'test',
        col4: 'test',
        col5: 'test',
        col6: 'test'
      },
      {
        col1: 'react-table',
        col2: 'rocks',
      },
      {
        col1: 'whatever',
        col2: 'you want',
      },
    ],
    []
  );

  const columns = React.useMemo(
    () => [
      {
        Header: 'Date',
        accessor: 'col1', // accessor is the "key" in the data
      },
      {
        Header: 'Merchant',
        accessor: 'col2',
      },
      ,
      {
        Header: 'Amount',
        accessor: 'col3',
      },
      ,
      {
        Header: 'Category',
        accessor: 'col4',
      },
      ,
      {
        Header: 'Description',
        accessor: 'col5',
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({ columns, data });

  function filters () {
    return(
          <div className="Filters">
            <ul class="DatesFiltersList">
            <li>
              <form action="">
                <label for="From">From:</label>
                <input type="date" id="" name=""/>
                <input type="submit"/>
              </form>
            </li>
            <li>
              <form action="">
                <label for="To">To:</label>
                <input type="date" id="" name=""/>
                <input type="submit"/>
              </form>
            </li>
          </ul>
          <ul class="TypeFiltersList">
            <li>
              <form onSubmit="">
                <label>
                  <input type="text" value="Merchant" onChange="" />
                </label>
                <input type="submit" value="Submit" />
            </form>
          </li>
            <li>
              <button>idk rn</button>
            </li>
          </ul>
          <ul class="CategoryTagList">
            <li>
              <select>
                {listData.getCategoriesList}
              </select>
            </li>
            <li>
              <select>
                {listData.getTagList}
              </select>
            </li>
            <li>
              <select>
                {listData.getCcList}
              </select>
            </li>
          </ul>
          <ul class="StatusList">
            <li>
              <label class="container">Unreported
                <input type="checkbox" checked="checked"/>
                <span class="checkmark"></span>
              </label>
            </li>
            <li>
              <label class="container">Open
                <input type="checkbox" checked="checked"/>
                <span class="checkmark"></span>
              </label>
            </li>
            <li>
              <label class="container">Processing
                <input type="checkbox" checked="checked"/>
                <span class="checkmark"></span>
              </label>
            </li>
            <li>
              <label class="container">Approved
                <input type="checkbox" checked="checked"/>
                <span class="checkmark"></span>
              </label>
            </li>
            <li>
              <label class="container">Reimbursed
                <input type="checkbox" checked="checked"/>
                <span class="checkmark"></span>
              </label>
            </li>
            <li>
              <label class="container">Closed
                <input type="checkbox" checked="checked"/>
                <span class="checkmark"></span>
              </label>
            </li>
            <li>
              <label class="container">Deleted
                <input type="checkbox" checked=""/>
                <span class="checkmark"></span>
              </label>
            </li> 
          </ul>
        </div>
    )
  }

  function table (){
    return(
      <table {...getTableProps()}>
      <thead>
        {headerGroups.map(headerGroup => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(column => (
              <th {...column.getHeaderProps()}>{column.render('Header')}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map(row => {
          prepareRow(row)
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map(cell => {
                return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
              })}
            </tr>
          )
        })}
      </tbody>
    </table>
    );
  }

  return (
    <div>
      {filters()}
      {table()}
    </div>
  )
}

export default Table;
