import React, { Component } from 'react';
import Sidebar from './components/sidebar';
import ExpensesBody from './components/expenses_body';
import Landing from './landing';
import { BrowserRouter as Router, Route} from  "react-router-dom";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
  }

  state = {users: []}

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(users => this.setState({ users }));
  }

  loggedIn() {
    //Create a t/f state to check if user is logged in or not
    return true;
  }

  render () {
    if(this.loggedIn()){return(
      // <Router>
        <div class="App">
        {this.state.users.map(user =>  <p key={user.id}>{user.username}</p>)}
          <Sidebar/>
          {/* <Route path="/" exact component={ExpensesBody}/> */}
          {/* will add function to switch between reports and this */}
          <ExpensesBody /> 
        </div>
      // </Router>
    );}
    else{
      return(
        <Landing/>
      );
    }
  }
}

export default App;
